mapper.select:
select
from news where is_del=0
and theme_id=#{themeId}
and title like concat('%',#{keyWord}, '%' )
order by news_date desc
limit #{start},#{size}

select count(id) from news where is_del=0
and theme_id=#{themeId}
and title like concat('%',#{keyWord}, '%' )

mapper.update:
update news
set is_del= 1
where id=#{id}
